import React, { Component } from 'react';
import Autosuggest from 'react-autosuggest';
import {cities} from './variables.js';
import Now from './Components/now';
import WeatherRow from './Components/weatherrow';
import ShitTag from './Components/shittag';
import WTF from './Components/wtf';
import ReactGA from 'react-ga';
import {isCity, getSuggestions, getSuggestionValue, renderSuggestion, renderSectionTitle, getSectionSuggestions, getCountry } from './searchFunc.js';
import {onResponse, getWeather, getForecast} from './weatherFunc.js';


export const Api_Key = "8c5021109e1918aa1c478ce6b4f53c27";
export let location = '';

// ReactGA.initialize('UA-136012452-1');
// ReactGA.pageview(window.location.pathname + window.location.search);

class App extends React.Component {

  state = {
    temperature: 'Loading', //the temperature doubles as a loading notification if the connection is slow
    city: 'London',
    country: 'GB',
    humidity: '',
    description: '',
    error: '',
    value: '',
    suggestions: [],
    message: '',
    subMessage: '',
  }

  constructor(props) {
    super(props);
    this.onResponse = onResponse.bind(this);
    this.getWeather = getWeather.bind(this);
    this.getForecast = getForecast.bind(this);


  }

  // getForecast = async (e) => {
  //   //getForecast uses city and country rather than location
  //   const api_call = await fetch(`//api.openweathermap.org/data/2.5/forecast?q=${this.state.city},${this.state.country}&units=metric&appid=${Api_Key}`); 
  //   let response = await api_call.json();
  //   //and we store the information
  //   if(this.state.city && this.state.country){
  //     this.setState({
  //       currentForecast: response
  //     })
  //   }
  // }

  //handle left and right click take care of horizontal scrolling on the weather forecast section. The CSS 'scroll-behavior: smooth;' makes this nice and juicy
  handleRightClick() {
    let scrollDistance = window.outerWidth/2;
    document.getElementById('weatherRow').scrollLeft += scrollDistance;
  }

  handleLeftClick() {
    let scrollDistance = window.outerWidth/2;
    document.getElementById('weatherRow').scrollLeft -= scrollDistance;
  }

  //as soon as the component mounts on page load, we trigger the API calls
  componentDidMount() {
    //initializeReactGA()
    this.getWeather()
  };

  //onChange deals with users using the search bar
  onChange = (event, { newValue, method }) => {
    this.setState({
      value: newValue
    });

    if (isCity(cities, 'name', newValue)) {
      this.setState({
        city: newValue,
        country: getCountry(cities, 'name', newValue)
      },
      () => this.getWeather()
      );
    }

  };

  //autosuggest needs this
  onSuggestionsFetchRequested = ({ value }) => {
    this.setState({
      suggestions: getSuggestions(value)
    });
  };

  onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };

  render() {
    const { value, suggestions } = this.state;
    const inputProps = {
      placeholder: this.state.city ? this.state.city : "Where the fuck do you live?", //we display the city if geolocation found our user, if not we demand to know where they live
      value,
      onChange: this.onChange
    };


    return (
      <div className={'weather'+this.state.icon}>
         <div className="wrapper">
            <ShitTag />
            <WTF />

            <Autosuggest 
              multiSection={true}
              suggestions={suggestions}
              onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
              onSuggestionsClearRequested={this.onSuggestionsClearRequested}
              getSuggestionValue={getSuggestionValue}
              renderSuggestion={renderSuggestion}
              renderSectionTitle={renderSectionTitle}
              getSectionSuggestions={getSectionSuggestions}
              inputProps={inputProps} 
            />
            <Now 
              temperature={this.state.temperature} 
              description={this.state.description} 
              windDeg={this.state.windDeg}
              windSpeed={this.state.windSpeed}
              daylightLeft={this.state.daylightLeft}
              sunrise={this.state.sunrise}
              sunset={this.state.sunset}
              message={this.state.message}
              subMessage={this.state.subMessage}
              icon={this.state.icon}
            />
            <WeatherRow 
              handleLeftClick={this.handleLeftClick}
              handleRightClick={this.handleRightClick}
              currentForecast={this.state.currentForecast}
            />
        </div>
      </div>
    )
  }
}
export default App;
